fn split_first(&self) -> (Option<char>, Self){
        let mut remaining = self.clone();
        (remaining.next(), remaining)
    }

parser::Input::<'i>::split_prefix:
#[inline]
pub fn split_prefix<P: Pattern>(&self, p: P) -> Option<Self>{
        let mut remaining = self.clone();
        if p.split_prefix(&mut remaining) {
            Some(remaining)
        } else {
            None
        }
    }

