/// // At no point in this example is an allocation performed.
/// let bytes = Vec::from(err.into_vec());
/// assert_eq!(bytes, B(b"foo\xFFbar"));
/// ```
pub struct FromUtf8Error {
    original: Vec<u8>,
    err: Utf8Error,
}

ext_vec::FromUtf8Error::as_bytes:
