/// let err = bytes.into_string().unwrap_err();
///
/// // At no point in this example is an allocation performed.
/// assert_eq!(err.as_bytes(), B(b"foo\xFFbar"));
/// ```
#[inline]
pub fn as_bytes(&self) -> &[u8]{
        &self.original
    }

