fn decode_sentence(bs: &[u8]) -> (&str, usize){
    if bs.is_empty() {
        ("", 0)
    } else if let Some(end) = SENTENCE_BREAK_FWD.find(bs) {
        // Safe because a match can only occur for valid UTF-8.
        let sentence = unsafe { bs[..end].to_str_unchecked() };
