    /// surrogate pair is not required.
    /// Violating this can easily lead to undefined behavior.
    pub unsafe fn from_tuple_unchecked(utf16: (u16,Option<u16>)) -> Self {
        Utf16Char { units: [utf16.0, utf16.1.unwrap_or(0)] }
