        match Utf16Char::from_str_start(&self.str[self.index..]) {
            Ok((u16c, bytes)) => {
                let item = (self.index, u16c);
                self.index += bytes;
                Some(item)
            },
            Err(EmptyStrError) => None
        }
    }

