            self.inner.next().map(|u16c| {
                let units = u16c.borrow().to_array();
                self.prev_second = units[1];
                units[0]
            })
        } else {
            let prev_second = self.prev_second;
            self.prev_second = 0;
            Some(prev_second)
        }
    }

